import './index.css';
import React, {useState} from 'react';
import StripeCheckout from "react-stripe-checkout"

function Home() {
  const [product, setProduct] = useState({
    name: "React from FB",
    price:10,
    productBy:"facebook"
  });

  const makePayment = token => {
    const body = {
      token,
      product,
    }
    const headers = {
      "Content-Type":"application/json",
    }

    return fetch(`http://localhost:8282/payment`, {
      method: "POST",
      headers,
      body: JSON.stringify(body)
    })
    .then(response => {
      console.log("Response: " + response);
      const {status} = response;
      console.log("Status" + status);
    })
    .catch(error => console.log(error));
  }

  return (
    <div className="App">
      <StripeCheckout stripeKey="pk_test_51JNn34EA934vxoltGlS1lzurj1QbG6dCdUvModYkoNb1qEcTXA6vHck3w8mnmuTj7aNXQkJn4a80wca5qKmRRONn00Q2PcPgbd" token={makePayment} name="Buy React" amount={product.price * 100} >
        <button className="btn-large buy-button">Buy Product {product.price}</button>
      </StripeCheckout>
    </div>
  );
}

export default Home;
