import React, { useState } from 'react'
import { useHistory } from 'react-router-dom';
import Paypal from './Paypal'
export default function PaypalPayment(props) {
    const [checkout,setCheckOut] = useState(false)
    const history = useHistory();
    const handleOnClick = () => history.push('/paypal-payment');
    return (
        <div>
            {/* display paypal payment options */}
            <img src="https://www.paypalobjects.com/webstatic/en_US/i/btn/png/btn_buynow_107x26.png" alt="Buy Now" />
            {checkout ? (
                <div>
                    {handleOnClick()}
                    <Paypal title={props.title} price={props.price}/>
                </div>
            ) : (
            // checkout button
            <img  src="https://www.paypalobjects.com/webstatic/en_US/i/btn/png/btn_buynow_107x26.png" alt="fdf"/>
            // <button onClick={() => {
            //     setCheckOut(true)
            // }}>
            //     Buy {props.title} ${props.price}
            // </button>
            )}
        </div>
    )
}
