import React, {useState} from 'react';
import {CardElement, useStripe, useElements} from '@stripe/react-stripe-js';
import MakePayment from './components/MakePayment'
import GenUser from './components/GenUser'
import './index.css';

export default function Home() {
    const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    // Block native form submission.
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not loaded yet. Make sure to disable
      // form submission until Stripe.js has loaded.
      return;
    }

    // Get a reference to a mounted CardElement. Elements knows how
    // to find your CardElement because there can only ever be one of
    // each type of element.
    const cardElement = elements.getElement(CardElement);

    // Use your card Element with other Stripe.js APIs
    const {error, paymentMethod} = await stripe.createPaymentMethod({
      type: 'card',
      card: cardElement,
    });

    if (error) {
      console.log('[error]', error);
    } else {
      console.log('[PaymentMethod]', paymentMethod);
    }
  };
  return (
    <div className="App">
            <form onSubmit={handleSubmit}>
      <CardElement />
      <button type="submit" disabled={!stripe}>
        Pay
      </button>
    </form>
        <div className="title">
            Sock Drive
        </div>
        <form>
            <div className="row product">
                <div className="row product-demo1">
                    <GenUser/>
                </div>
                <div className="row buy">
                    <MakePayment name="James" price="10" title="Book"/>
                </div>
                <div className="desc">
                    <p className="description">
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. <br/> 
                        Itaque illum veritatis ullam, ut iste rem. <br/> 
                        Vel animi doloribus unde quasi dolorem culpa iste. <br/> 
                        Molestiae dicta modi tenetur officia omnis earum?
                    </p>
                </div>
            </div>
            <div className="row product">
                <div className="row product-demo2">
                    <GenUser/>
                </div>
                <div className="row buy">
                    <MakePayment name="Aidan" price="35" title="Book"/>
                </div>
                <div className="col desc">
                    <p className="col description">
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. <br/> 
                        Itaque illum veritatis ullam, ut iste rem. <br/> 
                        Vel animi doloribus unde quasi dolorem culpa iste. <br/> 
                        Molestiae dicta modi tenetur officia omnis earum?
                    </p>
                </div>
            </div>
        </form>
    </div>
  );
}
