import React, { useState } from 'react'
import Paypal from './Paypal'
import './index.css';
export default function PaypalPayment() {
    const [checkout,setCheckOut] = useState(false)
    return (
        <div>
            {/* if checkout clicked then render paypal component, 
            else render checkout paypal button */}
            {checkout ? (
                <div>
                    <Paypal/>
                </div>
            ) : (
            <button onClick={() => {
                setCheckOut(true)
            }}>
                Checkout
            </button>
            )}
        </div>
    )
}
